INSERT INTO `check_item_def` VALUES ('6', '6.1.4', '6.1', '建筑物室外市政消防供水', 'B', '应采用两路市政给水网供水(除建筑高度超过 54m的住宅外，室外消火栓设计流量≤20L/s 时，可采用一路消防供水)', null, null, null, null);
