INSERT INTO `check_item_def` VALUES ('9', '6.2.2', '6.2', '消防水池有效容积、格数', 'B', '应符合规范及设计的要求', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('10', '6.2.3', '6.2', '室外消防水池取水口与建筑物的距离', 'B', '取水口(井)与建筑物(水泵房除外)的距离不 宜小于15m', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('11', '6.2.4', '6.2', '室外消防水池取水口与液体储罐的距离', 'B', '取水口（井）与甲、乙、丙类液体储罐等构 筑物的距离不宜小于40m', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('12', '6.2.5', '6.2', '室外消防水池取水口吸水高度', 'B', '不应大于6.0m', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('13', '6.2.6', '6.2', '消防用水与其他用水共用水池的技术措 施', 'B', '应采取确保消防用水量不作他用的技术措施', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('14', '6.2.7', '6.2', '消防水池出水管', 'B', '应保证消防水池的有效容积能被全部利用', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('15', '6.2.8', '6.2', '消防水池水位显示装置', 'C', '应设置就地水位显示装置，并应在消防控制 中心或值班室等地点设置显示消防水池水位 的装置，同时应有最高和最低报警水位', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('16', '6.2.9', '6.2', '消防水池的溢流水管、排水设施', 'C', '消防水池应设置溢流水管和排水设施，并应 采用间接排水', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('17', '6.3', '6', '消防水箱', null, null, null, null, null, null);
INSERT INTO `check_item_def` VALUES ('18', '6.3.1', '6.3', '高位消防水箱的设置(室内采用临时高 压消防给水系统)', 'B', '应按规范及设计要求设置', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('19', '6.3.2', '6.3', '消防水箱有效容积', 'B', '应符合规范及设计要求，并应满足初期火灾 消防用水量的要求', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('20', '6.3.3', '6.3', '高位消防水箱设置位置', 'B', '应高于其所服务的水灭火设施，且最低有效 水位应满足水灭火设施最不利点处的静水压 力；当不能满足静压要求时，应设稳压泵', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('21', '6.3.4', '6.3', '消防水箱间有管道侧面主要通道宽度', 'C', '净距不宜小于1.0m，且管道外壁与建筑物本 体墙面之间的通道宽度不宜小于0.6m', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('22', '6.3.5', '6.3', '消防水箱无管道侧面检修通道', 'C', '净距不宜小于0.7m', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('23', '6.3.6', '6.3', '消防水箱顶部至板底距离', 'C', '设有人孔的水箱顶，其顶面与其上面的建筑 物本体板底的净空不应小于0.8m', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('24', '6.3.7', '6.3', '消防水箱自动供水设施设置', 'B', '补水设施应正常(应设水泵自动启停装置或浮 球阀等自动供水设施)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('25', '6.3.8', '6.3', '消防水箱进水管设置', 'B', '水箱进水管的管径应满足消防水箱8h充满水 的要求，但管径不应小于DN32', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('26', '6.3.9', '6.3', '消防水箱出水管设置', 'B', '高位消防水箱出水管管径应满足消防给水设 计流量的出水要求，且不应小于DN100', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('27', '6.3.10', '6.3', '消防水箱出水管止回阀设置', 'B', '高位消防水箱出水管应位于水箱最低水位以 下，并应设置防止消防用水进入高位消防水 箱的止回阀', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('28', '6.3.11', '6.3', '消防水箱的溢流水管、排水设施', 'C', '消防水箱应设置溢流水管和排水设施，并应 采用间接排水', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('29', '6.3.12', '6.3', '消防水箱水位显示装置', 'C', '应设置就地水位显示装置，并应在消防控制 中心或值班室等地点设置显示消防水箱水位 的装置，同时应有最高和最低报警水位', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('30', '6.3.13', '6.3', '消防用水与其他用水共用水池的技术措 施', 'B', '应采取确保消防用水量不作他用的技术措施', null, null, null, null);
