INSERT INTO `check_item_def` VALUES ('33', '6.4.2', '6.4', '消防水泵接合器的设置(其他水灭火系 统含干式消火栓系统）', 'B', '自动喷水灭火系统、水喷雾灭火系统、泡沫 灭火系统、固定消防炮灭火系统等水灭火系 统，均应设置消防水泵接合器', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('34', '6.4.3', '6.4', '水泵接合器设置位置', 'B', '应设在室外便于消防车使用的地点，且距室 外消火栓或消防水池的距离不宜小于15m，并 不宜大于40m', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('35', '6.4.4', '6.4', '水泵接合器数量', 'B', '应按系统设计流量经计算确定', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('36', '6.4.5', '6.4', '水泵接合器标志', 'B', '应设置永久性标志铭牌，并应标明供水系统、供水范围和额定压力', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('37', '6.4.6', '6.4', '水泵接合器止回阀安装方向', 'B', '应使消防用水能从水泵接合器进入系统', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('38', '6.4.7', '6.4', '水泵接合器安全阀安装及管径', 'B', '公称压力1.6MPa和2.5MPa的接合器，安全阀 的公称通径应不小于20mm；公称压力4.0MPa 的接合器，安全阀的公称通径应不小于25mm', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('39', '6.4.8', '6.4', '地下水泵接合器的安装', 'C', '应使进水口与井盖底面的距离不大于0.40m， 且不应小于井盖的半径', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('40', '6.4.9', '6.4', '地下水泵接合器标志', 'B', '应采用铸有“消防水泵接合器”标志的铸铁 井盖', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('41', '6.4.10', '6.4', '墙壁水泵接合器与门窗距离', 'C', '与墙面上的门、窗、孔、洞的净距离不应小 于2.0m，且不应安装在玻璃幕墙下方', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('42', '6.4.11', '6.4', '墙壁水泵接合器安装高度', 'C', '接口至室外地面的距离宜为0.7m', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('43', '6.4.12', '6.4', '水泵接合器口径', 'B', '公称通径100mm的接合器应选用DN65mm外螺纹 固定接口；公称通径150mm的接合器应选用 DN80mm外螺纹固定接口', null, null, null, null);
