INSERT INTO `check_item_def` VALUES ('46', '7.1.1', '7.1', '消防水泵设置及选型', 'A', '应按设计要求设置，选型应满足消防给水系 统的流量和压力需求', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('47', '7.1.2', '7.1', '消防水泵备用泵的设置', 'B', '消防水泵应设置备用泵(除建筑高度小于54m 的住宅和室外消防给水设计流量≤25L/s的建 筑、室内消防给水设计流量≤10L/s的建筑外', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('48', '7.1.3', '7.1', '水泵控制柜', 'B', '消防水泵控制柜在平时应使消防水泵处于自 动启泵状态，应注明所属系统编号的标志， 按钮、指示灯及仪表应正常', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('49', '7.1.4', '7.1', '主备泵的切换', 'A', '主泵不能正常投入运行时,应自动切换启动备 用泵', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('50', '7.1.5', '7.1', '水泵外观质量及安装质量', 'C', '泵及电机的外观表面不应有碰损，轴心不应 有偏心；水泵之间及其与墙或其他设备之间 的间距应满足安装、运行、维护管理要求', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('51', '7.1.6', '7.1', '消防水泵标志', 'C', '消防水泵应有注明系统名称和编号的标志牌', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('52', '7.1.7', '7.1', '水泵启停控制方式', 'A', '消防水泵不应设置自动停泵的控制功能，应 能手动启停和自动启动', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('53', '7.1.8', '7.1', '消防水泵启动时间', 'B', '消防水泵应确保从接到启泵信号到水泵正常 运转的自动启动时间不应大于2min', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('54', '7.1.9', '7.1', '现场手动启泵功能', 'A', '消防水泵、稳压泵应设置就地强制启停泵按 钮，并应有保护装置', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('55', '7.1.10', '7.1', '稳压泵技术性能', 'A', '应满足系统自动启动和管网充满水的要求， 启动运行应正常,启泵与停泵压力应符合设定 值,压力表显示应正常', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('56', '7.1.11', '7.1', '稳压泵启停控制', 'B', '稳压泵应由消防给水管网或气压水罐上设置 的稳压泵自动启停泵压力开关或压力变送器 控制。当消防主泵启动时，稳压泵应停止运 行', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('57', '7.1.12', '7.1', '消防控制室手动直接启动消防泵功能', 'A', '消防控制室内的消防联动控制器应直接手动 控制消防泵的启动、停止', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('58', '7.1.13', '7.1', '水泵动作信号反馈', 'A', '消防泵的启动和停止的动作信号应反馈至消 防联动控制器', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('59', '7.1.14', '7.1', '水泵故障信号反馈', 'B', '水泵发生故障时,应有信号反馈回消防控制室', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('60', '7.1.15', '7.1', '水泵吸水方式', 'B', '系统的供水泵、稳压泵，应采用自灌式吸水 方式。采用天然水源时，水泵的吸水口应采 取防止杂物堵塞的措施', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('61', '7.1.16', '7.1', '水泵控制阀', 'C', '进出口阀门应常开，标志牌应正确', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('62', '7.1.17', '7.1', '流量、压力试验装置', 'C', '一组消防水泵应在消防水泵房内设置流量和 压力测试装置。单台泵流量不应大于20L/s， 设计工作压力不应大于0.50MPa，应预留流量 计和压力计接口，并应符合设计要求', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('63', '7.1.18', '7.1', '消防泵出水管上的试水管', 'C', '每台消防泵出水管上应设置DN65的试水管， 并应采取排水措施', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('64', '7.1.19', '7.1', '水泵吸水管管径', 'C', '应满足当其中一条吸水管损坏或检修时，其 余吸水管应仍能通过全部消防给水设计流量', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('65', '7.1.20', '7.1', '水泵出水管管径', 'C', '应满足当其中一条输水管发生故障时，其余 输水管应仍能供应全部消防给水设计流量', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('66', '7.1.21', '7.1', '水泵出水管附件安装', 'C', '出水管上应设止回阀、明杆闸阀；当采用蝶 阀时，应带有自锁装置', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('67', '7.1.22', '7.1', '水泵吸水管附件安装', 'C', '消防水泵吸水管上应设置明杆闸阀或带自锁 装置的蝶阀，当设置暗杆阀门时应设有开启 刻度和标志', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('68', '7.1.23', '7.1', '防超压措施', 'B', '防超压措施应正常', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('69', '7.1.24', '7.1', '气压罐型号', 'B', '气压水罐有效容积、气压、水位及设计压力 应符合设计要求', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('70', '7.1.25', '7.1', '气压罐安装', 'C', '安装间距、管道安装应符合设计要求', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('71', '7.1.26', '7.1', '气压罐出水管', 'C', '气压罐出水管上应设止回阀', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('72', '7.2', '7', '管网', null, null, null, null, null, null);
INSERT INTO `check_item_def` VALUES ('73', '7.2.1', '7.2', '管材', 'B', '管材及压力等级应符合规范及设计要求，管 材、管件内外涂层不应有脱落、锈蚀，表面 无划痕、无裂痕', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('74', '7.2.2', '7.2', '室内消火栓竖管管径', 'C', '应符合设计要求，但不应小于DN100', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('75', '7.2.3', '7.2', '防晃支架设置', 'C', '架空管道每段管道设置的防晃支架不应少于1 个；立管应在其始端和终端设防晃支架或采 用管卡固定', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('76', '7.2.4', '7.2', '套管与管道间隙处理', 'C', '消防给水管穿过墙体或楼板时应加套管，套 管与管道的间隙应采用不燃材料填塞', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('77', '7.2.5', '7.2', '管道颜色', 'C', '架空管道外应刷红色油漆或涂红色环圈标志', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('78', '7.2.6', '7.2', '吸水管条数', 'C', '一组消防水泵，吸水管不应少于两条，当其 中一条损坏或检修时，其余吸水管应仍能通 过全部消防给水设计流量', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('79', '7.2.7', '7.2', '进水管数量', 'A', '向室内外环状消防给水管网供水的输水干管、一组消防泵向环状管网的输水干管均不应 少于2条，当其中一条输水管发生故障时，其 余输水管应仍能供应全部消防给水设计流量', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('80', '7.2.8', '7.2', '给水管网', 'B', '室内消火栓系统管网应布置成环状(除室外消 火栓设计流量不大于20L/s，且室内消火栓不 超过10个时外)；宜与其他水灭火系统的管网 分开设置，当合用消防泵时，供水管路沿水 流方向应在报警阀前分开设置', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('81', '7.2.9', '7.2', '室内给水管道上阀门设置', 'B', '应按规范及设计要求设置', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('82', '7.2.10', '7.2', '阀门启闭标志', 'C', '应有明显启闭标志', null, null, null, null);
