INSERT INTO `check_item_def` VALUES ('1318', '23.1.8', '23.1', '储存装置容器阀检修机构', 'B', '容器阀检修机构应常开，并有锁止机构锁住 (CECS 345:2013 7.0.1.6)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1319', '23.2', '23', '探火管', null, null, null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1320', '23.2.1', '23.2', '探火管的标志', 'B', '探火管外表面应隔断1m设有永久性标识：型 号规格、静态动作温度、生产日期、生产单 位或商标等（GA 1167-2014 6.3.1）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1321', '23.2.2', '23.2', '探火管敷设', 'B', '应按设计要求敷设，并应采用专用管夹固定，固定措施应保证探火管牢固、工作可靠； 当被保护对象为电线电缆时，应将探火管随 电线电缆敷设，并应用专用的管夹固定 (CECS 345:2013  5.2.4.2及其条文说明)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1322', '23.2.3', '23.2', '探火管穿过墙壁或设备壳体时的保护措 施', 'C', '应采用专用保护件或连接件，防止探火管磨 损(CECS 345:2013  5.2.4.4)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1323', '23.2.4', '23.2', '探火管安装的温度要求', 'B', '不应布置在温度大于80℃的物体表面(CECS 345:2013  5.2.4.5)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1324', '23.2.5', '23.2', '探火管检漏压力表', 'B', '探火管管道上应设检漏压力表，压力表用来 显示探火管内的压力示值，其标度盘应设红 绿区，且指针应指向绿区范围内(CECS 345:2013  4.2.5、7.0.1及其条文说明)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1325', '23.2.6', '23.2', '探火管压力表安装位置', 'C', '应便于观察(CECS 345:20135.2.4.6)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1326', '23.2.7', '23.2', '探火管内压力要求', 'B', '探火管内充气压力不应小于1.0MPa，其压力 确保容器阀处于关闭状态(CECS 345:2013 4.2.2、 7.0.1条文说明)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1327', '23.2.8', '23.2', '局部应用灭火工程的探火管布置', 'B', '探火管与保护对象之间不应有遮挡物(CECS 345:2013  3.1.7.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1328', '23.2.9', '23.2', '局部应用灭火工程围封结构与保护对象 距离', 'B', '直接式探火管灭火装置保护对象应有实际围 封结构，且任一面距保护对象的最大距离不 应大于1.0m(CECS 345:2013  3.1.7.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1329', '23.2.10', '23.2', '探火管与保护对象间距（正上方布置）', 'C', '探火管宜布置在保护对象的正上方，且距离 不应大于600mm(CECS 345:2013  3.1.10)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1330', '23.2.11', '23.2', '探火管与保护对象间距（侧方或下方布 置）', 'C', '当探火管布置在保护对象的侧方或下方时， 其距离不应大于160mm(CECS 345:20133.1.10)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1331', '23.2.12', '23.2', '探火管弯曲半径', 'C', '不宜小于其外径的15倍(CECS 345:20133.1.10)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1332', '23.2.13', '23.2', '探火管之间的距离', 'C', '不应大于1.0m(CECS 345:2013  3.1.10)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1333', '23.3', '23', '防护区及控制方式', null, null, null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1334', '23.3.1', '23.3', '直接式七氟丙烷探火管灭火装置防护区 容积', 'B', '全淹没直接式七氟丙烷探火管灭火装置保护 的防护区最大单体容积不应大于6m3(CECS 345:2013  3.1.4.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1335', '23.3.2', '23.3', '直接式二氧化碳防护区容积', 'B', '全淹没直接式二氧化碳探火管灭火装置保护 的防护区最大单体容积不应大于3m3(CECS 345:2013  3.1.4.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1336', '23.3.3', '23.3', '直接式探火管灭火装置保护对象个数', 'B', '全淹没1套直接式探火管灭火装置保护的防护 区或保护对象不宜大于6个(CECS 345:20133.1.8)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1337', '23.3.4', '23.3', '间接式探火管灭火装置防护区容积', 'B', '全淹没间接式探火管灭火装置保护的防护区 最大单体容积不应大于60m3(CECS345:2013  3.1.4.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1338', '23.3.5', '23.3', '1个防护区设置的间接式探火管灭火装 置数量', 'B', '1个防护区设置的间接式探火管灭火装置不应 超过4套，并应能同时启动(CECS 345:20133.1.8)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1339', '23.3.6', '23.3', '间接式探火管的控制方式', 'B', '间接式探火管灭火装置应有自动控制和机械 应急操作两种控制方式(CECS 345:20133.3.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1340', '23.3.7', '23.3', '直接式探火管的控制方式', 'B', '直接式探火管灭火装置可采用自动控制一种 方式(CECS 345:2013  3.3.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1341', '23.3.8', '23.3', '探火管灭火装置动作信号反馈', 'A', '应提供灭火装置释放信号；当设有消防控制 室时，宜提供有关信号给消防控制室(CECS 345:2013  3.3.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1342', '24', '0', '厨房设备灭火系统', null, null, null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1343', '24.1', '24', '贮存装置', null, null, null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1344', '24.1.1', '24.1', '厨房设备灭火系统设置', 'A', '餐厅建筑面积大于1000m2的餐馆或食堂，其 烹饪操作间的排油烟罩及烹饪部位应设置自 动灭火装置，并应在燃气或燃油管道上设置 与自动灭火装置联动的自动切断装置（GB 50016-2014 8.3.11', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1345', '24.1.2', '24.1', '灭火剂贮存容器外观', 'C', '应无明显加工缺陷或无机械损伤、防腐涂层 应完整均匀，紧固件应无松动（GA 498-2012 5.1.1.1）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1346', '24.1.3', '24.1', '灭火剂贮存容器标志', 'C', '明显部位应设置耐久性铭牌，外表面应用中 文标注灭火剂名称、充装质量及有效使用期（GA 498-2012 5.1.1.2）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1347', '24.1.4', '24.1', '贮存装置的安装', 'C', '安装应固定牢固,手动操作装置的防护罩、铅 封和安全标志应完整，并应符合设计要求 (CECS233:2007 4.0.2、6.2.1.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1348', '24.1.5', '24.1', '贮存装置机械应急操作部位的安装高度', 'C', '距地面的高度宜为1.5m(CECS233:2007 4.0.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1349', '24.1.6', '24.1', '贮压式灭火剂贮存容器压力显示器', 'C', '贮压式灭火剂贮存容器组件应装有显示其内 部压力的显示器（GA 498-2012 5.2.7）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1350', '24.1.7', '24.1', '驱动气瓶外观', 'C', '应无明显加工缺陷或无机械损伤、防腐涂层 应完整均匀，紧固件应无松动（GA 498-2012 5.1.1.1）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1351', '24.1.8', '24.1', '驱动气瓶标志', 'C', '驱动气瓶应标出驱动气体名称和充装质量（ 或压力）（GA 498-2012 5.1.1.2）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1352', '24.1.9', '24.1', '驱动气体贮存容器压力显示器', 'C', '驱动气体贮存容器组件应装有显示其内部压 力的显示器（GA 498-2012 5.2.7）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1353', '24.1.10', '24.1', '驱动气体贮存容器的充装压力', 'B', '不宜大于6.0MPa(CECS233:2007 3.4.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1354', '24.1.11', '24.1', '贮压式灭火剂贮存容器和驱动气体贮存 容器内的压力要求', 'B', '不得小于设计贮存压力的90%(CECS233:2007 6.2.1.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1355', '24.1.12', '24.1', '厨房设备灭火装置的手动操作装置的标 志', 'C', '厨房设备灭火装置的手动操作装置和相关阀 门处应设置清晰明显的标志(CECS233:2007 3.5.5)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1356', '24.1.13', '24.1', '厨房设备灭火装置的检修阀', 'C', '厨房设备灭火装置处于正常工作状态时，检 修阀应处于开启状态(CECS233:2007 3.2.6)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1357', '24.2', '24', '控制装置', null, null, null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1358', '24.2.1', '24.2', '控制盘报警功能', 'A', '控制盘应能接收感温器或压力信号阀（器） 发来的报警信号，发出声光警报（GA 498-2012 5.3.2）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1359', '24.2.2', '24.2', '控制盘电源', 'A', '主、备电源应能自动切换供电，且延续工作 时间不应小于24h,并应有工作指示（GA 498-2012 5.3.1.b、c CECS233:3.5.4、B.3.1）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1360', '24.2.3', '24.2', '控制盘（电启动式）自动、手动功能', 'A', '有自动、手动启动灭火装置功能，应有明显 标志并可相互转换。无论控制盘处于自动或 手动状态，手动操作启动应始终有效（GA 498-2012 5.3.3.1.a）', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1361', '24.2.4', '24.2', '控制盘（电启动式）延迟启动功能', 'B', '有延迟启动功能，延迟时间应在0s～30s范围 内可调(GA498-2012 5.3.3.1.b)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1362', '24.2.5', '24.2', '控制盘（电启动式）反馈信号显示功能', 'B', '灭火装置启动的反馈信号显示功能(GA498- 2012 5.3.3.1.d)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1363', '24.2.6', '24.2', '控制盘（电启动式）提供接线端子', 'B', '提供控制外部设备的接线端子、装置启动信 号传送给消防控制中心的接线端子(GA 498-2012 5.3.3.1.e)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1364', '24.2.7', '24.2', '控制盘（电启动式）自身状态显示功能', 'B', '具备自身（包括探测、控制回路）故障报警 显示功能(GA498-2012 5.3.3.1.f)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1365', '24.2.8', '24.2', '控制盘（机械启动式）反馈信号显示功 能', 'B', '灭火装置启动的反馈信号显示功能以及燃气 联动阀关闭信号的显示功能(GA 498-2012 5.3.3.2.a)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1366', '24.2.9', '24.2', '控制盘（机械启动式）提供接线端子', 'B', '提供控制外部设备的接线端子、装置启动信 号传送给消防控制中心的接线端子(GA 498-2012 5.3.3.2.b)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1367', '24.3', '24', '防护区', null, null, null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1368', '24.3.1', '24.3', '喷嘴的设置', 'B', '应设置在灶具上部的中心轴线处。烹饪设备 的每个灶具上部应设置喷嘴(CECS233:2007 3.2.1、3.2.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1369', '24.3.2', '24.3', '喷嘴防堵要求', 'C', '应有防止灰尘或油脂堵塞喷孔的防护装置,且 应在喷放灭火剂时自动打开(CECS233:2007 3.4.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1370', '24.3.3', '24.3', '排烟罩喷嘴设置', 'C', '保护排烟罩的喷嘴应设置在滤油网板的上部, 并宜采用水平喷放方式(CECS233:2007 3.2.4)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1371', '24.3.4', '24.3', '排烟管道喷嘴设置', 'B', '每个烟道进口端应设置至少1只向排烟管道内 喷放灭火剂的喷嘴(CECS233:2007 3.2.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1372', '24.3.5', '24.3', '排烟管道的保护长度', 'B', '保护长度应自距离排烟管道延伸段最近的烟 道进口端算起,向内延伸不小于 6m(CECS233:2007 3.1.4)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1373', '24.3.6', '24.3', '感温器的设置', 'B', '烹饪设备的每个灶具上部应设置感温器 (CECS233:2007 3.2.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1374', '24.3.7', '24.3', '感温器的安装位置', 'C', '应便于检查和清洗，感温器与保护对象之间 不应有遮挡物(CECS233:2007 4.0.6.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1375', '24.3.8', '24.3', '感温器的电启动导线保护', 'B', '感温器采用电启动时,导线应采用阻燃线管或 金属套管保护,且不应裸露在与火焰直接接触 的部位(CECS233:2007 3.4.4)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1376', '24.3.9', '24.3', '感温器的易熔合金导线保护', 'B', '感温器采用易熔合金等机械启动方式时,拉索 应采用不锈钢多股绳。且应采用内外经防腐 处理的钢管保护,拉索转弯处应采用专用滑轮 (CECS233:2007 3.4.4)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1377', '24.3.10', '24.3', '燃气联动阀安装位置', 'C', '安装于厨房灶台燃气管路上(GA 498-20123.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1378', '24.3.11', '24.3', '水流联动阀安装位置', 'C', '安装于厨房设备灭火装置管路上并与水源连 接(GA 498-2012 3.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1379', '24.3.12', '24.3', '灭火剂输送管道', 'B', '宜采用不锈钢无缝钢管，不应使用碳钢管和 复合管(CECS233:2007 3.4.5.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1380', '24.3.13', '24.3', '驱动气体输送管道', 'B', '应采用铜管或高压软管(CECS233:2007 3.4.5.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1381', '24.3.14', '24.3', '管道固定支、吊架间距', 'C', '管道应设固定支、吊架，其间距不应大于 2.5m(CECS233:2007 3.4.6)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1382', '24.3.15', '24.3', '厨房设备灭火装置启动方式', 'B', '机械启动式的装置应具有自动启动和机械应 急启动；电启动式的装置应具有自动启动、 手动启动和机械应急启动(GA498-2012 5.1.2.1、5.1.2.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1383', '24.3.16', '24.3', '燃气联动阀功能试验', 'B', '当厨房设备灭火装置启动时能立即联动关闭 (GA 498-2012 3.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1384', '24.3.17', '24.3', '水流联动阀功能试验', 'B', '当厨房设备灭火装置启动、灭火剂完全喷射 后，能立即联动开启,自动切换喷放冷却水 (GA 498-2012 3.3、CECS233:2007 3.5.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1385', '24.3.18', '24.3', '多套厨房设备灭火装置启动要求', 'B', '一个防护单元采用多套厨房设备灭火装置保 护时，应保证这些灭火装置及其所有喷头灭 火时同时启动、喷放灭火剂(CECS233:2007 3.1.2、3.1.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1386', '24.3.19', '24.3', '感温器(电动型、机械型)启动功能试验', 'A', '触发感温器，声光报警，燃气阀关闭，反馈 信号应正常，联动设备动作正确 (CECS233:2007 B.1.1、B.1.2、B.1.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1387', '25', '0', '注氮控氧防火系统', null, null, null, null, null, null);
