INSERT INTO `check_item_def` VALUES ('1539', '27.6.19', '27.6', '泡沫比例混合器安装-备用环泵式', 'C', '备用环泵式泡沫比例混合器应并联安装再系 统上,并应有明显的标志(GB 50281-20065.4.2.2)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1540', '27.6.20', '27.6', '泡沫比例混合器安装-压力式', 'C', '压力式泡沫比例混合器装置应整体安装,并应 与基础牢固固定，泡沫液储罐的单罐容积不 应大于10m3（GB 50151-2010 3.4.4.1 、GB50281-2006 5.4.3)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1541', '27.6.21', '27.6', '泡沫比例混合器安装-平衡式', 'C', '平衡式泡沫比例混合器装置应竖直安装在压 力水的水平管道上,泡沫液进口压力应大于水 进口压力(GB 50151-2010 3.4.2.1 、GB50281-2006 5.4.4.1)', null, null, null, null);
INSERT INTO `check_item_def` VALUES ('1542', '27.6.22', '27.6', '泡沫比例混合器安装-管线式', 'C', '应靠近储罐或防护区,其吸液口与泡沫液罐或 泡沫液桶最低液面的高度不得大于1.0m(GB 50281-2006 5.4.5)', null, null, null, null);
